(86016, 'wpcode_notifications', 'a:4:{s:6:\"update\";i:1723948032;s:4:\"feed\";a:1:{i:0;a:8:{s:5:\"title\";s:71:\"Introducing 2 NEW Conditional Logic rules & Dynamic Content Auto Insert\";s:7:\"content\";s:299:\"In our commitment to providing you with precise control over code snippet placement, we\'re introducing two new Conditional Logic rules: Post/Page by Authors and Page Templates. Plus, new Dynamic Content Auto-Insert Locations allowing you to insert snippets at specific intervals within your content.\";s:4:\"type\";a:6:{i:0;s:5:\"basic\";i:1;s:6:\"bundle\";i:2;s:5:\"elite\";i:3;s:4:\"lite\";i:4;s:4:\"plus\";i:5;s:3:\"pro\";}s:2:\"id\";i:66;s:4:\"icon\";s:7:\"success\";s:4:\"btns\";a:1:{s:4:\"main\";a:2:{s:3:\"url\";s:101:\"https://wpcode.com/introducing-wpcode-221/?utm_source=plugin&utm_medium=notification&utm_campaign=221\";s:4:\"text\";s:10:\"Learn More\";}}s:5:\"start\";s:19:\"2024-08-16 04:00:00\";s:3:\"end\";s:19:\"2024-08-23 04:00:00\";}}s:6:\"events\";a:0:{}s:9:\"dismissed\";a:0:{}}', 'off'),
