(3479, 'wpcode_snippets', 'a:4:{s:10:\"admin_only\";a:2:{i:0;a:13:{s:2:\"id\";i:4891;s:5:\"title\";s:29:\"Add ID column in admin tables\";s:4:\"code\";s:759:\"add_action( \'admin_init\', function () {\r\n// Get all public post types\r\n	$post_types = get_post_types( array(), \'names\' );\r\n\r\n	function wpcode_add_post_id_column( $columns ) {\r\n		$columns[\'wpcode_post_id\'] = \'ID\'; // \'ID\' is the column title\r\n\r\n		return $columns;\r\n	}\r\n\r\n	function wpcode_show_post_id_column_data( $column, $post_id ) {\r\n		if ( \'wpcode_post_id\' === $column ) {\r\n			echo \'<code>\' . absint( $post_id ) . \'</code>\';\r\n		}\r\n	}\r\n\r\n	foreach ( $post_types as $post_type ) {\r\n		// Add new column to the posts list\r\n		add_filter( \"manage_{$post_type}_posts_columns\", \'wpcode_add_post_id_column\' );\r\n\r\n		// Fill the new column with the post ID\r\n		add_action( \"manage_{$post_type}_posts_custom_column\", \'wpcode_show_post_id_column_data\', 10, 2 );\r\n	}\r\n} );\";s:9:\"code_type\";s:3:\"php\";s:8:\"location\";s:10:\"admin_only\";s:11:\"auto_insert\";i:1;s:13:\"insert_number\";i:1;s:9:\"use_rules\";b:0;s:5:\"rules\";a:0:{}s:8:\"priority\";i:10;s:14:\"location_extra\";s:0:\"\";s:20:\"shortcode_attributes\";a:0:{}s:8:\"modified\";s:19:\"2024-08-18 04:26:16\";}i:1;a:13:{s:2:\"id\";i:633;s:5:\"title\";s:34:\"Lien de publication/page en double\";s:4:\"code\";s:3775:\"// Add duplicate button to post/page list of actions.\r\nadd_filter( \'post_row_actions\', \'wpcode_snippet_duplicate_post_link\', 10, 2 );\r\nadd_filter( \'page_row_actions\', \'wpcode_snippet_duplicate_post_link\', 10, 2 );\r\n\r\n// Let\'s make sure the function doesn\'t already exist.\r\nif ( ! function_exists( \'wpcode_snippet_duplicate_post_link\' ) ) {\r\n	/**\r\n	 * @param array   $actions The actions added as links to the admin.\r\n	 * @param WP_Post $post The post object.\r\n	 *\r\n	 * @return array\r\n	 */\r\n	function wpcode_snippet_duplicate_post_link( $actions, $post ) {\r\n\r\n		// Don\'t add action if the current user can\'t create posts of this post type.\r\n		$post_type_object = get_post_type_object( $post->post_type );\r\n\r\n		if ( null === $post_type_object || ! current_user_can( $post_type_object->cap->create_posts ) ) {\r\n			return $actions;\r\n		}\r\n\r\n\r\n		$url = wp_nonce_url(\r\n			add_query_arg(\r\n				array(\r\n					\'action\'  => \'wpcode_snippet_duplicate_post\',\r\n					\'post_id\' => $post->ID,\r\n				),\r\n				\'admin.php\'\r\n			),\r\n			\'wpcode_duplicate_post_\' . $post->ID,\r\n			\'wpcode_duplicate_nonce\'\r\n		);\r\n\r\n		$actions[\'wpcode_duplicate\'] = \'<a href=\"\' . $url . \'\" title=\"Duplicate item\" rel=\"permalink\">Duplicate</a>\';\r\n\r\n		return $actions;\r\n	}\r\n}\r\n\r\n/**\r\n * Handle the custom action when clicking the button we added above.\r\n */\r\nadd_action( \'admin_action_wpcode_snippet_duplicate_post\', function () {\r\n\r\n	if ( empty( $_GET[\'post_id\'] ) ) {\r\n		wp_die( \'No post id set for the duplicate action.\' );\r\n	}\r\n\r\n	$post_id = absint( $_GET[\'post_id\'] );\r\n\r\n	// Check the nonce specific to the post we are duplicating.\r\n	if ( ! isset( $_GET[\'wpcode_duplicate_nonce\'] ) || ! wp_verify_nonce( $_GET[\'wpcode_duplicate_nonce\'], \'wpcode_duplicate_post_\' . $post_id ) ) {\r\n		// Display a message if the nonce is invalid, may it expired.\r\n		wp_die( \'The link you followed has expired, please try again.\' );\r\n	}\r\n\r\n	// Load the post we want to duplicate.\r\n	$post = get_post( $post_id );\r\n\r\n	// Create a new post data array from the post loaded.\r\n	if ( $post ) {\r\n		$current_user = wp_get_current_user();\r\n		$new_post     = array(\r\n			\'comment_status\' => $post->comment_status,\r\n			\'menu_order\'     => $post->menu_order,\r\n			\'ping_status\'    => $post->ping_status,\r\n			\'post_author\'    => $current_user->ID,\r\n			\'post_content\'   => $post->post_content,\r\n			\'post_excerpt\'   => $post->post_excerpt,\r\n			\'post_name\'      => $post->post_name,\r\n			\'post_parent\'    => $post->post_parent,\r\n			\'post_password\'  => $post->post_password,\r\n			\'post_status\'    => \'draft\',\r\n			\'post_title\'     => $post->post_title . \' (copy)\',// Add \"(copy)\" to the title.\r\n			\'post_type\'      => $post->post_type,\r\n			\'to_ping\'        => $post->to_ping,\r\n		);\r\n		// Create the new post\r\n		$duplicate_id = wp_insert_post( $new_post );\r\n		// Copy the taxonomy terms.\r\n		$taxonomies = get_object_taxonomies( get_post_type( $post ) );\r\n		if ( $taxonomies ) {\r\n			foreach ( $taxonomies as $taxonomy ) {\r\n				$post_terms = wp_get_object_terms( $post_id, $taxonomy, array( \'fields\' => \'slugs\' ) );\r\n				wp_set_object_terms( $duplicate_id, $post_terms, $taxonomy );\r\n			}\r\n		}\r\n		// Copy all the custom fields.\r\n		$post_meta = get_post_meta( $post_id );\r\n		if ( $post_meta ) {\r\n\r\n			foreach ( $post_meta as $meta_key => $meta_values ) {\r\n				if ( \'_wp_old_slug\' === $meta_key ) { // skip old slug.\r\n					continue;\r\n				}\r\n				foreach ( $meta_values as $meta_value ) {\r\n					add_post_meta( $duplicate_id, $meta_key, $meta_value );\r\n				}\r\n			}\r\n		}\r\n\r\n		// Redirect to edit the new post.\r\n		wp_safe_redirect(\r\n			add_query_arg(\r\n				array(\r\n					\'action\' => \'edit\',\r\n					\'post\'   => $duplicate_id\r\n				),\r\n				admin_url( \'post.php\' )\r\n			)\r\n		);\r\n		exit;\r\n	} else {\r\n		wp_die( \'Error loading post for duplication, please try again.\' );\r\n	}\r\n} );\r\n\";s:9:\"code_type\";s:3:\"php\";s:8:\"location\";s:10:\"admin_only\";s:11:\"auto_insert\";i:1;s:13:\"insert_number\";i:1;s:9:\"use_rules\";b:0;s:5:\"rules\";a:0:{}s:8:\"priority\";i:10;s:14:\"location_extra\";s:0:\"\";s:20:\"shortcode_attributes\";a:0:{}s:8:\"modified\";s:19:\"2024-08-18 04:26:17\";}}s:11:\"frontend_cl\";a:2:{i:0;a:13:{s:2:\"id\";i:1545;s:5:\"title\";s:35:\"Désactiver l\'API REST de WordPress\";s:4:\"code\";s:252:\"add_filter(\r\n	\'rest_authentication_errors\',\r\n	function ( $access ) {\r\n		return new WP_Error(\r\n			\'rest_disabled\',\r\n			__( \'The WordPress REST API has been disabled.\' ),\r\n			array(\r\n				\'status\' => rest_authorization_required_code(),\r\n			)\r\n		);\r\n	}\r\n);\";s:9:\"code_type\";s:3:\"php\";s:8:\"location\";s:11:\"frontend_cl\";s:11:\"auto_insert\";i:1;s:13:\"insert_number\";i:1;s:9:\"use_rules\";b:1;s:5:\"rules\";a:0:{}s:8:\"priority\";i:10;s:14:\"location_extra\";s:0:\"\";s:20:\"shortcode_attributes\";a:0:{}s:8:\"modified\";s:19:\"2024-08-18 04:26:17\";}i:1;a:13:{s:2:\"id\";i:470;s:5:\"title\";s:27:\"Completely Disable Comments\";s:4:\"code\";s:1239:\"add_action(\'admin_init\', function () {\r\n    // Redirect any user trying to access comments page\r\n    global $pagenow;\r\n    \r\n    if ($pagenow === \'edit-comments.php\') {\r\n        wp_safe_redirect(admin_url());\r\n        exit;\r\n    }\r\n\r\n    // Remove comments metabox from dashboard\r\n    remove_meta_box(\'dashboard_recent_comments\', \'dashboard\', \'normal\');\r\n\r\n    // Disable support for comments and trackbacks in post types\r\n    foreach (get_post_types() as $post_type) {\r\n        if (post_type_supports($post_type, \'comments\')) {\r\n            remove_post_type_support($post_type, \'comments\');\r\n            remove_post_type_support($post_type, \'trackbacks\');\r\n        }\r\n    }\r\n});\r\n\r\n// Close comments on the front-end\r\nadd_filter(\'comments_open\', \'__return_false\', 20, 2);\r\nadd_filter(\'pings_open\', \'__return_false\', 20, 2);\r\n\r\n// Hide existing comments\r\nadd_filter(\'comments_array\', \'__return_empty_array\', 10, 2);\r\n\r\n// Remove comments page in menu\r\nadd_action(\'admin_menu\', function () {\r\n    remove_menu_page(\'edit-comments.php\');\r\n});\r\n\r\n// Remove comments links from admin bar\r\nadd_action(\'init\', function () {\r\n    if (is_admin_bar_showing()) {\r\n        remove_action(\'admin_bar_menu\', \'wp_admin_bar_comments_menu\', 60);\r\n    }\r\n});\";s:9:\"code_type\";s:3:\"php\";s:8:\"location\";s:11:\"frontend_cl\";s:11:\"auto_insert\";i:1;s:13:\"insert_number\";i:1;s:9:\"use_rules\";b:1;s:5:\"rules\";a:0:{}s:8:\"priority\";i:10;s:14:\"location_extra\";s:0:\"\";s:20:\"shortcode_attributes\";a:0:{}s:8:\"modified\";s:19:\"2024-08-18 04:26:19\";}}s:10:\"everywhere\";a:3:{i:0;a:13:{s:2:\"id\";i:576;s:5:\"title\";s:58:\"Désactiver l\'URL du site Web du formulaire de commentaire\";s:4:\"code\";s:161:\"add_filter( \'comment_form_default_fields\', function ($fields) {\r\n	if ( isset( $fields[\'url\'] ) ) {\r\n		unset( $fields[\'url\'] );\r\n	}\r\n\r\n	return $fields;\r\n}, 150 );\";s:9:\"code_type\";s:3:\"php\";s:8:\"location\";s:10:\"everywhere\";s:11:\"auto_insert\";i:1;s:13:\"insert_number\";i:1;s:9:\"use_rules\";b:0;s:5:\"rules\";a:0:{}s:8:\"priority\";i:10;s:14:\"location_extra\";s:0:\"\";s:20:\"shortcode_attributes\";a:0:{}s:8:\"modified\";s:19:\"2024-08-18 04:26:18\";}i:1;a:13:{s:2:\"id\";i:473;s:5:\"title\";s:24:\"Disable Attachment Pages\";s:4:\"code\";s:651:\"add_action(\r\n	\'template_redirect\',\r\n	function () {\r\n		global $post;\r\n		if ( ! is_attachment() || ! isset( $post->post_parent ) || ! is_numeric( $post->post_parent ) ) {\r\n			return;\r\n		}\r\n\r\n		// Does the attachment have a parent post?\r\n		// If the post is trashed, fallback to redirect to homepage.\r\n		if ( 0 !== $post->post_parent && \'trash\' !== get_post_status( $post->post_parent ) ) {\r\n			// Redirect to the attachment parent.\r\n			wp_safe_redirect( get_permalink( $post->post_parent ), 301 );\r\n		} else {\r\n			// For attachment without a parent redirect to homepage.\r\n			wp_safe_redirect( get_bloginfo( \'wpurl\' ), 302 );\r\n		}\r\n		exit;\r\n	},\r\n	1\r\n);\r\n\";s:9:\"code_type\";s:3:\"php\";s:8:\"location\";s:10:\"everywhere\";s:11:\"auto_insert\";i:1;s:13:\"insert_number\";i:1;s:9:\"use_rules\";b:0;s:5:\"rules\";a:0:{}s:8:\"priority\";i:10;s:14:\"location_extra\";s:0:\"\";s:20:\"shortcode_attributes\";a:0:{}s:8:\"modified\";s:19:\"2024-08-18 04:26:19\";}i:2;a:13:{s:2:\"id\";i:472;s:5:\"title\";s:22:\"Allow SVG Files Upload\";s:4:\"code\";s:1769:\"/**\r\n * Allow SVG uploads for administrator users.\r\n *\r\n * @param array $upload_mimes Allowed mime types.\r\n *\r\n * @return mixed\r\n */\r\nadd_filter(\r\n	\'upload_mimes\',\r\n	function ( $upload_mimes ) {\r\n		// By default, only administrator users are allowed to add SVGs.\r\n		// To enable more user types edit or comment the lines below but beware of\r\n		// the security risks if you allow any user to upload SVG files.\r\n		if ( ! current_user_can( \'administrator\' ) ) {\r\n			return $upload_mimes;\r\n		}\r\n\r\n		$upload_mimes[\'svg\']  = \'image/svg+xml\';\r\n		$upload_mimes[\'svgz\'] = \'image/svg+xml\';\r\n\r\n		return $upload_mimes;\r\n	}\r\n);\r\n\r\n/**\r\n * Add SVG files mime check.\r\n *\r\n * @param array        $wp_check_filetype_and_ext Values for the extension, mime type, and corrected filename.\r\n * @param string       $file Full path to the file.\r\n * @param string       $filename The name of the file (may differ from $file due to $file being in a tmp directory).\r\n * @param string[]     $mimes Array of mime types keyed by their file extension regex.\r\n * @param string|false $real_mime The actual mime type or false if the type cannot be determined.\r\n */\r\nadd_filter(\r\n	\'wp_check_filetype_and_ext\',\r\n	function ( $wp_check_filetype_and_ext, $file, $filename, $mimes, $real_mime ) {\r\n\r\n		if ( ! $wp_check_filetype_and_ext[\'type\'] ) {\r\n\r\n			$check_filetype  = wp_check_filetype( $filename, $mimes );\r\n			$ext             = $check_filetype[\'ext\'];\r\n			$type            = $check_filetype[\'type\'];\r\n			$proper_filename = $filename;\r\n\r\n			if ( $type && 0 === strpos( $type, \'image/\' ) && \'svg\' !== $ext ) {\r\n				$ext  = false;\r\n				$type = false;\r\n			}\r\n\r\n			$wp_check_filetype_and_ext = compact( \'ext\', \'type\', \'proper_filename\' );\r\n		}\r\n\r\n		return $wp_check_filetype_and_ext;\r\n\